package controller;import model.entity.SandWich;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.servlet.config.annotation.EnableWebMvc;@Controller@EnableWebMvc@RequestMapping("foodController")public class FoodController {    @RequestMapping("views")    public String viewsChoise(Model model) {        SandWich sandWich = new SandWich();        model.addAttribute("sandWich");        return "views";    }    @GetMapping("save")    public String viewsChoise1(Model model,@ModelAttribute("sandWich") SandWich sandWich) {        System.out.println(sandWich);        model.addAttribute("sandWich",sandWich);        return "notify";    }}